import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';  // Para navegar a otra página
import { getPetDetails, updatePet } from '../../services/api';  // Usamos las funciones desde api.js
import '../../styles/PetDashboard.css';

const PetDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [pet, setPet] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState('FOREST');
  //const [selectedAccessory, setSelectedAccessory] = useState('NONE');
  //const [loading, setLoading] = useState(true);

  // Fetch the pet details from the API when the component is mounted

    const fetchPetDetails = async () => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId'); // Obtener el userId desde localStorage
      if (!token || !userId) {
        console.error("Token or userId not found!");
        navigate('/signin');
        return;
      }

      try {
        const response = await getPetDetails(userId, petIdFromUrl, token);  // Solo pasamos el petId y userId
        if (response && response.data) {
          setPet(response.data); // Guardamos los detalles de la mascota
          setLoading(false);  // Cambiamos a false cuando los detalles se han cargado
        }
      } catch (err) {
        console.error('Error fetching pet details:', err);
        setLoading(false);
      }
    };

  const handleLocationChange = (e) => {
    setSelectedLocation(e.target.value); // Cambia el valor de la localización
    if (pet && pet.petId) {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId'); // Obtener el userId desde localStorage
      if (token && userId) {
        // Construimos el objeto para el body de la solicitud
        const petUpdateRequest = {
          location: e.target.value,
        };

        // Llamamos a la API de actualización para la localización
        updatePet(pet.petId, userId, petUpdateRequest, token)
          .then((response) => {
            console.log("Location updated successfully:", response.data);
            setPet(response.data); // Actualizamos el estado con los nuevos datos de la mascota
          })
          .catch((err) => {
            console.error("Error updating location:", err);
          });
      }
    }
  };

  return (
    <div className="pet-dashboard-container">
      <div className="pet-dashboard-content">
        <div className="pet-details-container">
          <h1>{pet.petName}</h1>
          <div className="pet-image-container">
            <img 
              src={require(`../../assets/pets/${pet.petType}_${pet.petColor}.png`)} 
              alt={pet.petName} 
              className="pet-image"
            />
             <img 
              src={require(`../../assets/backgrounds/${selectedLocation}.jpg`)} 
              alt={`background of ${pet.petName}`} 
              className="pet-background-image"
            />
          </div>
          <p>Type: {pet.petType}</p>
          <p>Color: {pet.petColor}</p>
          <p>Happiness: {pet.happiness}</p>
          <p>Hunger: {pet.hunger}</p>
          <p>Created on: {pet.createdAt}</p>
        </div>
      </div>
    </div>
  );
};

export default PetDashboard;

