import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Usamos useNavigate para redirigir
import { createPet } from '../../services/api'; // Asegúrate de importar la función para crear la mascota

const CreatePet = () => {
  const [petName, setPetName] = useState('');
  const [petType, setPetType] = useState('');
  const [petColor, setPetColor] = useState('');
  const navigate = useNavigate(); // Usamos navigate para redirigir al usuario a la mascota creada

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Lógica para crear la mascota
    try {
      const petData = { petName, petType, petColor };
      const token = localStorage.getItem('token'); // Obtener el token de autenticación
      const response = await createPet(petData, token);  // Llamada a la API para crear la mascota
      const createdPetId = response.data.id; // Obtener el ID de la mascota creada

      // Redirigir a la página de la mascota creada con el ID correspondiente
      navigate(`/pet/${createdPetId}`); // Usamos el ID de la mascota para redirigir al detalle
    } catch (err) {
      console.error('Error creating pet:', err);
    }
  };

  return (
    <div className="create-pet-container">
      <div className="form-container">
        <h1>Create a New Pet</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Name</label>
            <input 
              type="text" 
              placeholder="Enter pet name" 
              value={petName}
              onChange={(e) => setPetName(e.target.value)}
            />
          </div>
          <div>
            <label>Pet Type</label>
            <select value={petType} onChange={(e) => setPetType(e.target.value)}>
              <option value="FROG">Frog</option>
              <option value="MUSHROOM">Mushroom</option>
              <option value="FAIRY">Fairy</option>
            </select>
          </div>
          <div>
            <label>Pet Color</label>
            <select value={petColor} onChange={(e) => setPetColor(e.target.value)}>
              <option value="GREEN">Green</option>
              <option value="RED">Red</option>
              <option value="PINK">Pink</option>
              <option value="BLUE">Blue</option>
            </select>
          </div>
          <button type="submit">Create Pet</button>
        </form>
      </div>
    </div>
  );
};

export default CreatePet;
