import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPetDetails, updatePet } from '../../services/api'; // Función para obtener y actualizar la mascota
import '../../styles/PetDashboard.css'; // Asegúrate de que este archivo CSS esté correctamente importado

const PetDashboard = () => {
  const { petId } = useParams(); // Obtén el petId desde la URL
  const userId = localStorage.getItem('userId'); // Obtén el userId desde el localStorage
  const [pet, setPet] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Para redirigir al usuario si es necesario

  const [selectedLocation, setSelectedLocation] = useState(pet?.location || 'FOREST');
  const [selectedAccessory, setSelectedAccessory] = useState(pet?.accessory || 'NONE');

  useEffect(() => {
    const fetchPetDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await getPetDetails(userId, petId, token); // Llamada a la API con userId y petId
        setPet(response.data);
      } catch (err) {
        console.error('Error fetching pet details:', err);
        setError('Failed to load pet details');
      }
    };

    fetchPetDetails();
  }, [petId, userId]);

  const handleLocationChange = (e) => {
    setSelectedLocation(e.target.value);
    // Aquí puedes llamar a un endpoint para actualizar la ubicación si lo deseas
  };

  const handleAccessoryChange = (e) => {
    setSelectedAccessory(e.target.value);
    // Aquí puedes actualizar la mascota con el accesorio seleccionado
  };

  const handleInteraction = (interactionType) => {
    // Lógica para interactuar con la mascota (PET, FEED)
    updatePet(pet.id, { interaction: interactionType }); // Llamar al endpoint para actualizar
  };

  if (!pet) {
    return <p>Loading...</p>;
  }

  return (
    <div className="pet-dashboard-container">
      <div className="pet-dashboard-content">
        <h1>{pet.petName}</h1>
        <div className="pet-image-container">
          <img 
            src={require(`../../assets/pets/${pet.petType}_${pet.petColor}.png`)} 
            alt={pet.petName} 
            className="pet-image"
          />
          <img 
            src={require(`../../assets/backgrounds/${selectedLocation}.jpg`)} 
            alt={`background of ${pet.petName}`} 
            className="pet-background-image"
          />
        </div>
        <div className="pet-details">
          <p>Type: {pet.petType}</p>
          <p>Color: {pet.petColor}</p>
          <p>Happiness: {pet.happiness}</p>
          <p>Hunger: {pet.hunger}</p>
          <p>Created on: {pet.createdAt}</p>
        </div>
        <div className="pet-interactions">
          <button onClick={() => handleInteraction('PET')}>Pet</button>
          <button onClick={() => handleInteraction('FEED')}>Feed</button>
          <select value={selectedLocation} onChange={handleLocationChange}>
            <option value="FOREST">Forest</option>
            <option value="LAKE">Lake</option>
            <option value="DESERT">Desert</option>
          </select>
          <select value={selectedAccessory} onChange={handleAccessoryChange}>
            <option value="NONE">None</option>
            <option value="LEAF">Leaf</option>
            <option value="CROWN">Crown</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default PetDashboard;
