const feedPet = async (petId) => {
    try {
        // Obtener la mascota actual (puedes obtener estos valores de un estado o contexto)
        const pet = getPetFromState(petId); // Método ficticio para obtener la mascota
        
        // Crear un objeto PetUpdateRequest con los cambios
        const updateData = {
            hunger: pet.hunger - 10,  // Reducir el hambre
            happiness: pet.happiness + 10,  // Aumentar la felicidad
        };

        // Enviar la solicitud PUT para actualizar la mascota
        const response = await fetch(`/api/pet/update/${petId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updateData),
        });

        if (response.ok) {
            const updatedPet = await response.json();
            // Aquí puedes actualizar el estado o hacer algo con la respuesta
            updatePetInState(updatedPet);
            alert('Mascota alimentada y feliz!');
        } else {
            alert('Hubo un problema al alimentar a la mascota.');
        }
    } catch (error) {
        console.error('Error al alimentar la mascota:', error);
    }
};

// Llamada al feedPet en el evento de clic de un botón
document.getElementById('feedButton').addEventListener('click', () => {
    const petId = 1; // ID de la mascota (esto lo deberías obtener dinámicamente)
    feedPet(petId);
});


