import React, { useState, useEffect } from 'react';
import { getUserPets } from '../../services/api'; // La función para obtener las mascotas
import { useNavigate } from 'react-router-dom';

const UserDashboard = () => {
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Obtener el userId del localStorage
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token'); // Obtener el token del localStorage

    // Si el token no existe, redirige al usuario a la página de inicio de sesión
    if (!token) {
      navigate('/signin');
      return;
    }

    // Llamar al endpoint para obtener las mascotas
    const fetchUserPets = async () => {
      try {
        const response = await getUserPets(userId, token);  // Pasar el token en los encabezados
        setPets(response.data);
      } catch (err) {
        console.error('Failed to load pets.', err);
        setError('Failed to load pets. No pets found. Please create a pet.');
      }
    };

    fetchUserPets();
  }, [navigate]);

  return (
    <div className="user-dashboard">
      <h1>My WhimsyPets</h1>
      {error && <p>{error}</p>}
      {pets.length > 0 ? (
        <div className="pets-container">
          {pets.map((pet) => {
            // Construcción de la ruta para la imagen de la mascota
            const petImage = pet.petType && pet.petColor ?
              require(`../../assets/pets/${pet.petType.toUpperCase()}_${pet.petColor.toUpperCase()}.webp`) :
              require(`../../assets/pets/default.webp`); // Imagen por defecto si no se encuentra la específica

            // Construcción de la ruta para la imagen de fondo (localización)
            const backgroundImage = pet.location ?
              require(`../../assets/backgrounds/${pet.location.toUpperCase()}.webp`) :
              require(`../../assets/backgrounds/default.webp`); // Imagen por defecto si no tiene ubicación

            return (
              <div key={pet.id} className="pet-card">
                <div className="pet-image-container">
                  {/* Imagen de la mascota */}
                  <img 
                    src={petImage} 
                    alt={pet.petName} 
                    className="pet-image"
                  />
                  <div className="pet-background">
                    {/* Fondo basado en la localización de la mascota */}
                    <img 
                      src={backgroundImage} 
                      alt={`background of ${pet.petName}`} 
                      className="pet-background-image"
                    />
                  </div>
                </div>
                <h3>{pet.petName}</h3>
                <div className="pet-status">
                  <div>
                    <label>Happiness</label>
                    <progress value={pet.happiness} max={100} />
                  </div>
                  <div>
                    <label>Hunger</label>
                    <progress value={pet.hunger} max={100} />
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <p>No pets found. Please create a pet.</p>
      )}
      <button onClick={() => navigate('/create-pet')}>Create New Pet</button>
    </div>
  );
};

export default UserDashboard;
