import React, { useEffect, useState } from 'react';
import { getUserPets } from '../../services/api'; // Asegúrate de que esta función esté definida correctamente
import '../../styles/UserDashboard.css'; // Verifica la ruta del archivo CSS

const UserDashboard = () => {
  // Definir las variables de estado correctamente
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');

  // Recupera el userId del localStorage
  const userId = localStorage.getItem('userId');

  // Función para obtener las mascotas del usuario
  const fetchUserPets = async () => {
    try {
      if (!userId) {
        setError('User not logged in.');
        return;
      }

      // Llamada a la API para obtener las mascotas
      const response = await getUserPets(userId); // Esta es la llamada correcta a la API
      console.log("Fetched pets:", response); // Verifica que 'response' tiene los datos
      setPets(response.data); // Asume que la respuesta tiene un campo 'data' que contiene las mascotas
    } catch (err) {
      console.error('Error fetching pets:', err);
      setError('Failed to load pets.'); // Guarda el error en el estado si ocurre uno
    }
  };

  // Llama a la función cuando el componente se monta
  useEffect(() => {
    fetchUserPets();
  }, []); // Se ejecuta solo una vez cuando el componente se monta

  return (
    <div className="user-dashboard">
      <h2>My WhimsyPets</h2>
      {error && <div>{error}</div>} {/* Muestra el mensaje de error si ocurre */}
      <div className="pet-list">
        {pets.length === 0 ? (
          <p>No pets found. Please create a pet.</p>
        ) : (
          pets.map((pet) => (
            <div key={pet.id} className="pet-card">
              <h3>{pet.name}</h3>
              <p>{pet.type}</p>
              {/* Agrega más detalles de la mascota si es necesario */}
            </div>
          ))
        )}
      </div>
      <button className="create-pet-btn">Create New Pet</button> {/* Botón para crear nuevas mascotas */}
    </div>
  );
};

export default UserDashboard;
