import React, { useState, useEffect } from 'react';
import { getUserPets } from '../../services/api'; // La función para obtener las mascotas
import { useNavigate } from 'react-router-dom';


const UserDashboard = () => {
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();


  useEffect(() => {
    // Obtener el userId del localStorage
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token'); // Obtener el token del localStorage


    // Si el token no existe, redirige al usuario a la página de inicio de sesión
    if (!token) {
      navigate('/signin');
      return;
    }


    // Llamar al endpoint para obtener las mascotas
    const fetchUserPets = async () => {
      try {
        const response = await getUserPets(userId, token);  // Pasar el token en los encabezados
        setPets(response.data);
      } catch (err) {
        console.error('Failed to load pets.', err);
        setError('Failed to load pets. No pets found. Please create a pet.');
      }
    };


    fetchUserPets();
  }, [navigate]);


  return (
    <div className="user-dashboard">
      <h1>My WhimsyPets</h1>
      {error && <p>{error}</p>}
      {pets.length > 0 ? (
        <div>
          {pets.map((pet) => (
            <div key={pet.id}>
              <h2>{pet.petName}</h2>
              <p>Type: {pet.petType}</p>
              <p>Color: {pet.petColor}</p>
              <p>Happiness: {pet.happiness}</p>
              <p>Hunger: {pet.hunger}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No pets found. Please create a pet.</p>
      )}
      <button onClick={() => navigate('/create-pet')}>Create New Pet</button>
    </div>
  );
};


export default UserDashboard;
