            <div className="bar-container">
              <p>Hunger</p>
              <div className="bar" style={{ width: `${pet.hunger}%` }}></div>
            </div>
          </div>
        </div>
  
        {/* Contenedor de la información a la derecha */}
        <div className="petdashboard-info-container">
          <p><strong>Created on:</strong> {pet.createdAt}</p>
          <p><strong>Location:</strong> {pet.location}</p>
          <p><strong>Accessory:</strong> {pet.accessory}</p>
  
          {/* Controles para cambiar ubicación y accesorio */}
          <div className="petdashboard-pet-interactions">
            <select value={selectedLocation} onChange={handleLocationChange}>
              <option value="FOREST">Forest</option>
              <option value="LAKE">Lake</option>
              <option value="COTTAGE">Cottage</option>
              <option value="HAUNTED_HOUSE">Haunted House</option>
            </select>
            <select value={selectedAccessory} onChange={handleAccessoryChange}>
              <option value="NONE">None</option>
              <option value="CROWN">Crown</option>
              <option value="FLOWER">Flower</option>
              <option value="LEAF">Leaf</option>
            </select>
          </div>
        </div>
      </div>
    </div>
  );
