import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createPet } from '../../services/api'; // Asegúrate de que esta función esté correctamente importada
import '../../styles/CreatePet.css'; // Asegúrate de que este archivo CSS esté correctamente importado

const CreatePet = () => {
  const [petName, setPetName] = useState('');
  const [petType, setPetType] = useState('');
  const [petColor, setPetColor] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Obtener el userId y el token del localStorage
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token');

    if (!userId || !token) {
      navigate('/signin');
      return;
    }

    const newPet = {
      petName,
      petType,
      petColor,
    };

    try {
      // Crear la nueva mascota
      await createPet(newPet, token);
      // Redirigir al usuario a la pantalla de detalles de la mascota
      navigate(`/pet-details?userId=${userId}&petId=1`); // Asegúrate de usar el petId correcto aquí
    } catch (err) {
      console.error('Error creating pet:', err);
      setError('Failed to create pet.');
    }
  };

  return (
    <div className="create-pet-container">
      <div className="form-container">
        <h1>Create a New Pet</h1>
        {error && <p>{error}</p>}
        <form onSubmit={handleSubmit}>
          <div>
            <label>Pet Name</label>
            <input
              type="text"
              placeholder="Enter pet name"
              value={petName}
              onChange={(e) => setPetName(e.target.value)}
            />
          </div>
          <div>
            <label>Pet Type</label>
            <select
              value={petType}
              onChange={(e) => setPetType(e.target.value)}
            >
              <option value="">Select Type</option>
              <option value="FROG">Frog</option>
              <option value="MUSHROOM">Mushroom</option>
              <option value="FAIRY">Fairy</option>
            </select>
          </div>
          <div>
            <label>Pet Color</label>
            <select
              value={petColor}
              onChange={(e) => setPetColor(e.target.value)}
            >
              <option value="">Select Color</option>
              <option value="GREEN">Green</option>
              <option value="RED">Red</option>
              <option value="BLUE">Blue</option>
              <option value="PINK">Pink</option>
            </select>
          </div>
          <button type="submit">Create Pet</button>
        </form>
      </div>
    </div>
  );
};

export default CreatePet;
