import React, { useState, useEffect } from 'react';
import { getAll } from '../../services/api'; // La función para obtener todas las mascotas
import { useNavigate } from 'react-router-dom';
import '../../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token'); // Obtener el token del localStorage

    // Si el token no existe, redirige al usuario a la página de inicio de sesión
    if (!token) {
      navigate('/signin');
      return;
    }

    // Llamar al endpoint para obtener todas las mascotas
    const fetchAllPets = async () => {
      try {
        const response = await getAll(token);  // Pasar el token en los encabezados
        setPets(response.data);
      } catch (err) {
        console.error('Failed to load pets.', err);
        setError('Failed to load pets. No pets found.');
      }
    };

    fetchAllPets();
  }, [navigate]);

  const getPetImage = (pet) => {
    const { petColor, petType, asleep } = pet;
  
    // Si está dormida, buscamos la imagen con el sufijo _SLEEPING
    const imageSuffix = asleep ? '_SLEEPING' : '';
  
    // Devolvemos la imagen con el sufijo según el estado
    return require(`../../assets/pets/${petType}_${petColor}${imageSuffix}.png`);
  };

  return (
    <div className="admin-dashboard">
      <div className="whimsy-logo-container">
        <h1 className="title-text">WhimsyGarden - Admin</h1>
      </div>
      {error && <p>{error}</p>}
      {pets.length > 0 ? (
        <div className="pets-container">
          {pets.map((pet) => (
            <div key={pet.id} className="pet-card">
              <div className="pet-image-container">
                <img
                  src={getPetImage(pet)}  // Llamamos a la función que determina la imagen
                  alt={pet.petName}
                  className="pet-image"
                />
                <img
                  src={require(`../../assets/backgrounds/${pet.location}.jpg`)} // Imagen de fondo según la localización
                  alt={`background of ${pet.petName}`}
                  className="pet-background-image"
                />
              </div>
              <div className="pet-name-container">
                <h2>{pet.petName}</h2>
              </div>
              <div className="pet-status">
                <div className="status-bar">
                  <label>Happiness</label>
                  <progress value={pet.happiness} max={100} />
                </div>
                <div className="status-bar">
                  <label>Hunger</label>
                  <progress value={pet.hunger} max={100} />
                </div>
              </div>
              <div className="user-name-container">
                <h3>User: {pet.userName}</h3>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No pets found. Please create a pet.</p>
      )}
      <button className="create-pet-button" onClick={() => navigate('/create-pet')}>
        Create New Pet
      </button>
    </div>
  );
};

export default AdminDashboard;
