import React, { useState, useEffect } from 'react';
import { getUserPets } from '../../services/api'; // La función para obtener las mascotas
import { useNavigate } from 'react-router-dom';
import '../../styles/UserDashboard.css';


const UserDashboard = () => {
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();


  useEffect(() => {
    // Obtener el userId del localStorage
    const userId = localStorage.getItem('userId');
    const token = localStorage.getItem('token'); // Obtener el token del localStorage


    // Si el token no existe, redirige al usuario a la página de inicio de sesión
    if (!token) {
      navigate('/signin');
      return;
    }


    // Llamar al endpoint para obtener las mascotas
    const fetchUserPets = async () => {
      try {
        const response = await getUserPets(userId, token);  // Pasar el token en los encabezados
        setPets(response.data);
      } catch (err) {
        console.error('Failed to load pets.', err);
        setError('Failed to load pets. No pets found. Please create a pet.');
      }
    };


    fetchUserPets();
  }, [navigate]);

  const getPetImage = (pet) => {
    // Formato de la imagen según el color, tipo y estado de la mascota
    const { petColor, petType, petState } = pet;
    return require(`../../assets/pets/${petColor}_${petType}.png`);
  };

  return (
    <div className="user-dashboard">
      <h1>My WhimsyPets</h1>
      {error && <p>{error}</p>}
      {pets.length > 0 ? (
        <div className="pets-container">
          {pets.map((pet) => (
            <div key={pet.id} className="pet-card">
               <div className="pet-image-container">
                <img
                  src={getPetImage(pet)}  // Llamamos a la función que determina la imagen
                  alt={pet.petName}
                  className="pet-image"
                />
              </div>
              <h2>{pet.petName}</h2>
              <div className="pet-status">
                <div className="status-bar">
                  <label>Happiness</label>
                  <progress value={pet.happiness} max={100} />
                </div>
                <div className="status-bar">
                  <label>Hunger</label>
                  <progress value={pet.hunger} max={100} />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No pets found. Please create a pet.</p>
      )}
      <button onClick={() => navigate('/create-pet')}>Create New Pet</button>
    </div>
  );
};

export default UserDashboard;
