import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

export const api = axios.create({
  baseURL: API_URL,
});

export const signup = (data) => api.post('/v1/auth/signup', data);
export const signin = (data) => api.post('/v1/auth/signin', data);

export const createPet = (petData, token) => {
  return axios.post(`${API_URL}/pet/new`, petData, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};

  export const getUserPets = (userId, token) => {
    return api.get(`/pet/getUserPets?userId=${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
  };

  export const getAll = (token) => 
    api.get('/pet/getAll', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    export const getPetDetails = (userId, petId, token) => {
      return api.get('/pet/get', {
        params: { userId, petId },
        headers: { Authorization: `Bearer ${token}` },
      });
    };

    export const updatePet = async (petId, petInteraction, token) => {
      try {
        // Aquí estamos configurando los datos que vamos a enviar en la solicitud PUT
        const data = { petInteraction };  // Aquí solo enviamos la interacción de la mascota

        // Realizamos el PUT con los datos y el petId
        const response = await api.put(`/pet/update/${petId}`, data, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        return response.data;  // Retornamos los datos de la respuesta (mascota actualizada)
      } catch (err) {
        console.error("Error updating pet:", err);
        throw new Error("Failed to update pet interaction.");
      }
    };
