import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Asegúrate de tener useParams para leer los parámetros de la URL
import { getPetDetails } from '../../services/api'; // Asegúrate de que la función getPetDetails esté correctamente importada

const PetDashboard = () => {
  const { petId } = useParams(); // Obtén el petId desde la URL usando useParams
  const userId = localStorage.getItem('userId'); // Obtén el userId desde el localStorage (ya lo tienes guardado)
  const [pet, setPet] = useState(null);
  const navigate = useNavigate(); // Para redirigir al usuario si es necesario

  useEffect(() => {
    const fetchPetDetails = async () => {
        try {
          const token = localStorage.getItem('token'); // Obtener el token para autenticar la petición
          const response = await getPetDetails(userId, id, token); // Llamada a la API con userId y petId
          console.log(response.data);
          setPet(response.data); // Guardamos los datos de la mascota en el estado
        } catch (err) {
          console.error('Error fetching pet details:', err);
        }
    };

    if (petId) {  // Solo hacemos la solicitud si petId está disponible
      fetchPetDetails();
    }
  }, [petId, userId]); // El useEffect se ejecutará cada vez que petId o userId cambien

  if (!pet) {
    return <p>Loading...</p>;
  }

  return (
    <div className="pet-detail-container">
      <h1>{pet.petName}</h1>
      <img 
        src={require(`../../assets/pets/${pet.petType}_${pet.petColor}.png`)} 
        alt={pet.petName} 
      />
      <p>Type: {pet.petType}</p>
      <p>Color: {pet.petColor}</p>
      <p>Happiness: {pet.happiness}</p>
      <p>Hunger: {pet.hunger}</p>
      {/* Cualquier otra información que desees mostrar */}
    </div>
  );
};

export default PetDashboard;
