import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createPet } from '../../services/api'; // Importa la función para crear la mascota
import '../../styles/CreatePet.css';

const CreatePet = () => {
  const [petName, setPetName] = useState('');
  const [petType, setPetType] = useState('');
  const [petColor, setPetColor] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validar que todos los campos estén completos
    if (!petName || !petType || !petColor) {
      setError('Please fill all the fields');
      return;
    }

    // Obtener el token del localStorage
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/signin');
      return;
    }

    // Llamar a la función para crear la mascota
    try {
      const petData = {
        petName,
        petType,
        petColor,
      };

      await createPet(petData, token);  // Enviar la solicitud a la API para crear la mascota
      navigate('/user-dashboard');  // Redirige al dashboard después de crear la mascota
    } catch (err) {
      console.error('Failed to create pet', err);
      setError('Failed to create pet. Please try again.');
    }
  };

  return (
    <div className="create-pet-page">
      <h1 className="title-text">Create New Pet</h1>

      {error && <p className="error-message">{error}</p>}

      <form className="create-pet-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="petName">Pet Name:</label>
          <input
            type="text"
            id="petName"
            value={petName}
            onChange={(e) => setPetName(e.target.value)}
            placeholder="Enter pet name"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="petType">Pet Type:</label>
          <select
            id="petType"
            value={petType}
            onChange={(e) => setPetType(e.target.value)}
            required
          >
            <option value="">Select a pet type</option>
            <option value="FROG">Frog</option>
            <option value="MUSHROOM">Mushroom</option>
            <option value="FAIRY">Fairy</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="petColor">Pet Color:</label>
          <select
            id="petColor"
            value={petColor}
            onChange={(e) => setPetColor(e.target.value)}
            required
          >
            <option value="">Select a color</option>
            <option value="GREEN">Green</option>
            <option value="RED">Red</option>
            <option value="PINK">Pink</option>
            <option value="BLUE">Blue</option>
          </select>
        </div>

        <button type="submit" className="submit-button">Create Pet</button>
      </form>
    </div>
  );
};

export default CreatePet;
