import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getPetDetails } from '../../services/api'; // Asegúrate de tener esta función

const PetDetail = () => {
  const { id } = useParams(); // Obtenemos el ID de la mascota desde la URL
  const [pet, setPet] = useState(null);

  useEffect(() => {
    // Obtener los detalles de la mascota
    const fetchPetDetails = async () => {
      try {
        const response = await getPetDetails(id);  // Llamamos a la API para obtener los detalles de la mascota
        setPet(response.data);
      } catch (err) {
        console.error('Error fetching pet details:', err);
      }
    };

    fetchPetDetails();
  }, [id]);

  if (!pet) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{pet.petName}</h1>
      <img src={require(`../../assets/pets/${pet.petType}_${pet.petColor}.png`)} alt={pet.petName} />
      <p>Type: {pet.petType}</p>
      <p>Color: {pet.petColor}</p>
      <p>Happiness: {pet.happiness}</p>
      <p>Hunger: {pet.hunger}</p>
      {/* Agrega aquí cualquier otra información de la mascota que quieras mostrar */}
    </div>
  );
};

export default PetDetail;
