document.addEventListener('DOMContentLoaded', function () {
  // Obtener el token JWT desde el almacenamiento local
  const token = localStorage.getItem('jwtToken');
  const userId = getUserIdFromToken(token); // Función que extrae el userId del token

  // Obtener el petId desde la URL (cuando se hace clic en una mascota desde el user dashboard)
  const urlParams = new URLSearchParams(window.location.search);
  const petId = urlParams.get('petId'); // Obtener el petId de la URL

  // Si no hay un petId, redirigir al user dashboard
  if (!petId) {
    window.location.href = '/userDashboard'; // Redirige si no hay petId
    return;
  }

  // Obtener la mascota por petId
  async function fetchPet(petId) {
    try {
      const response = await fetch(`/api/pet/get?userId=${userId}&petId=${petId}`);
      const pet = await response.json();
      displayPet(pet);
    } catch (error) {
      console.error("Error fetching pet:", error);
    }
  }

  // Mostrar la mascota en el UI
  function displayPet(pet) {
    const petContainer = document.getElementById("pet-container");
    const petName = document.getElementById("pet-name");
    const petImage = document.getElementById("pet-image");
    const petHappiness = document.getElementById("pet-happiness");
    const petHunger = document.getElementById("pet-hunger");
    const petBackground = document.getElementById("pet-background");
    petName.textContent = pet.petName;
    petHappiness.textContent = `Felicidad: ${pet.happiness}`;
    petHunger.textContent = `Hambre: ${pet.hunger}`;
    petImage.src = `/images/${pet.petType.toUpperCase()}_${pet.petColor.toUpperCase()}.png`;

    // Cambiar el fondo según la localización
    petBackground.style.backgroundImage = `url(/images/${pet.location.toUpperCase()}.jpg)`;

    // Llenar selectores de localización y accesorios
    const locationSelect = document.getElementById("location-select");
    const accessoriesSelect = document.getElementById("accessories-select");
    locationSelect.innerHTML = `<option value="${pet.location}">${pet.location}</option>`;
    accessoriesSelect.innerHTML = `<option value="${pet.accessory}">${pet.accessory}</option>`;
  }

  // Actualizar la mascota (localización, accesorio)
  async function updatePet(updatedData) {
    try {
      const response = await fetch(`/api/pet/update/${petId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(updatedData)
      });
      const updatedPet = await response.json();
      displayPet(updatedPet);
    } catch (error) {
      console.error("Error updating pet:", error);
    }
  }

  // Interacciones con la mascota
  async function interactWithPet(interactionType) {
    const updatedData = {
      interaction: interactionType
    };
    await updatePet(updatedData);
  }

