  function getUserIdFromToken(token) {
    if (!token) return null;
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.userId; // Asegúrate de que el JWT contenga un userId
  }

  // Cargar la mascota al iniciar
  fetchPet(petId);
});

