import axios from 'axios';
import jwtDecode from 'jwt-decode';

const API_URL = 'http://localhost:8080/api';

export const api = axios.create({
  baseURL: API_URL,
});

const getToken = () => {
  return localStorage.getItem('token');
};

const getUserIdFromToken = () => {
  const token = getToken();
  if (token) {
      const decoded = jwtDecode(token); // Decodificar el token
      return decoded.userId; // Asumiendo que el JWT tiene el campo `userId`
  }
  return null;
};

export const signup = (data) => api.post('/v1/auth/signup', data);
export const signin = (data) => api.post('/v1/auth/signin', data);

export const createPet = (petData, token) => {
  return axios.post(`${API_URL}/pet/new`, petData, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};

  export const getUserPets = (userId, token) => {
    return api.get(`/pet/getUserPets?userId=${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
  };

  export const getAll = (token) => 
    api.get('/pet/getAll', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    export const getPetDetails = (petId) => {
      const token = getToken();
      const userId = getUserIdFromToken(); // Obtener el userId del token
      if (!token || !userId) {
          console.error("User not authenticated or token missing.");
          return Promise.reject("Authentication required");
      }

      return axios.get(`/api/pet/get?userId=${userId}&petId=${petId}`, {
          headers: { Authorization: `Bearer ${token}` },
      });
  };

  export const updatePet = (petId, petInteraction) => {
    const token = getToken();
    const userId = getUserIdFromToken(); // Obtener el userId del token

    if (!token || !userId) {
        console.error("User not authenticated or token missing.");
        return Promise.reject("Authentication required");
    }

    const data = { petInteraction, userId }; // Incluir el userId en el body

    return axios.put(`/api/pet/update/${petId}`, data, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });
};
