import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

export const api = axios.create({
  baseURL: API_URL,
});

const getUserIdFromStorage = () => {
  return localStorage.getItem('userId'); 
};


export const signup = (data) => api.post('/v1/auth/signup', data);
export const signin = (data) => api.post('/v1/auth/signin', data);

export const createPet = (petData, token) => {
  return axios.post(`${API_URL}/pet/new`, petData, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};

  export const getUserPets = (userId, token) => {
    return api.get(`/pet/getUserPets?userId=${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
  };

  export const getAll = (token) => 
    api.get('/pet/getAll', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    export const updatePet = (petId, updateData, token) => {
      const userId = getUserIdFromStorage(); // Obtenemos el userId directamente
      if (!token || !userId) {
        console.error("User not authenticated or token missing.");
        return Promise.reject("Authentication required");
      }

      // Creamos el objeto con los datos para la solicitud
      const data = {
        userId: userId,
        location: updateData.location || null,
        accessory: updateData.accessory || null,
        petInteraction: updateData.petInteraction || null, // Usamos la interacci贸n como una propiedad
      };

      return axios.put(`/pet/update/${petId}`, data, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
    };

    // Funci贸n para obtener los detalles de la mascota
    export const getPetDetails = (userId, petId, token) => {
      return api.get('/pet/get', {
        params: { userId, petId },
        headers: { Authorization: `Bearer ${token}` },
      });
    };
