import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { getPetDetails, updatePet } from '../../services/api'; 
import '../../styles/PetDashboard.css';

const PetDashboard = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const userId = params.get('userId');
  const petId = params.get('petId');  // Obtener el petId de la URL

  const [pet, setPet] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState('FOREST');
  const [selectedAccessory, setSelectedAccessory] = useState('NONE');

  useEffect(() => {
    if (!petId || !userId) {
      console.error("Pet ID or User ID is missing from the URL.");
      return; // Si no hay petId o userId, no se hace la solicitud
    }

    const fetchPetDetails = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error("Token not found!");
        return; // Si no se encuentra el token, no se hace la solicitud
      }

      try {
        const response = await getPetDetails(userId, petId, token); 
        if (response && response.data) {
          setPet(response.data);  // Guardamos los datos de la mascota en el estado
        } else {
          console.error("No pet data received from the API.");
        }
      } catch (err) {
        console.error("Error fetching pet details:", err);
      }
    };

    fetchPetDetails();
  }, [petId, userId]);  // Dependencias para recargar los detalles de la mascota

  // Asegurarse de que pet está cargado antes de intentar cambiar la localización o el accesorio
  const handleLocationChange = (e) => {
    setSelectedLocation(e.target.value);
    if (pet && pet.id) {
      const token = localStorage.getItem('token');
      if (token) {
        updatePet(pet.id, { location: e.target.value }, token);  // Usamos pet.id correctamente
      }
    }
  };

  const handleAccessoryChange = (e) => {
    setSelectedAccessory(e.target.value);
    if (pet && pet.id) {
      const token = localStorage.getItem('token');
      if (token) {
        updatePet(pet.id, { accessory: e.target.value }, token);  // Usamos pet.id correctamente
      }
    }
  };

  const handleInteraction = async (interactionType) => {
    if (pet && pet.id) {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          let petInteractionData = {};
  
          switch (interactionType) {
            case 'PET':
              console.log('Petting the pet!');
              petInteractionData = { petInteraction: 'PET' };
              break;
            case 'FEED':
              console.log('Feeding the pet!');
              petInteractionData = { petInteraction: 'FEED' };
              break;
            default:
              console.error('Unknown interaction type:', interactionType);
              break;
          }
  
          // Ahora se realiza el update usando la nueva función
          await updatePet(pet.id, petInteractionData, token);  // Aquí usamos la función que hemos definido en api.js
          console.log(`Pet ${interactionType} interaction successful!`);
        } catch (err) {
          console.error("Error updating pet:", err);
        }
      }
    }
  };

  if (!pet) {
    return <p>Loading...</p>;
  }

  return (
    <div className="pet-dashboard-container">
      <div className="pet-dashboard-content">
        <div className="pet-details-container">
          <h1>{pet.petName}</h1>
          <div className="pet-image-container">
            <img 
              src={require(`../../assets/pets/${pet.petType}_${pet.petColor}.png`)} 
              alt={pet.petName} 
              className="pet-image"
            />
            <img 
              src={require(`../../assets/backgrounds/${selectedLocation}.jpg`)} 
              alt={`background of ${pet.petName}`} 
              className="pet-background-image"
            />
          </div>
          <p>Type: {pet.petType}</p>
          <p>Color: {pet.petColor}</p>
          <p>Happiness: {pet.happiness}</p>
          <p>Hunger: {pet.hunger}</p>
          <p>Created on: {pet.createdAt}</p>
        </div>

        {/* Botones de interacción */}
        <div className="pet-interactions">
          <button onClick={() => handleInteraction('PET')}>Pet</button>
          <button onClick={() => handleInteraction('FEED')}>Feed</button>
          <select value={selectedLocation} onChange={handleLocationChange}>
            <option value="FOREST">Forest</option>
            <option value="LAKE">Lake</option>
            <option value="COTTAGE">Cottage</option>
            <option value="HAUNTED_HOUSE">Haunted House</option>
          </select>
          <select value={selectedAccessory} onChange={handleAccessoryChange}>
            <option value="NONE">None</option>
            <option value="LEAF">Leaf</option>
            <option value="CROWN">Crown</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default PetDashboard;
