import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

export const api = axios.create({
  baseURL: API_URL,
});

export const signup = (data) => api.post('/v1/auth/signup', data);
export const signin = (data) => api.post('/v1/auth/signin', data);

export const createPet = (data, token) =>
  api.post('/pet/new', data, {
    headers: { Authorization: `Bearer ${token}` },
  });

  export const getUserPets = (userId, token) => api.get(`/pet/getUserPets?userId=${userId}`, {
    headers: { Authorization: `Bearer ${token}` }
  });
